import React from "react";

const LibrarySong = ({ song, setCurrentSong, songs }) => {
  const selectSong = () => {
    setCurrentSong(song);
  };

  return (
    <div onClick={selectSong} className="library-song">
      <img src={song.cover} alt={song.name} />
      <div className="song-description">
        <h3>{song.name}</h3>
        <h4>{song.artist}</h4>
      </div>
    </div>
  );
};

export default LibrarySong;
